import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line, Bar } from 'react-chartjs-2';
import 'chart.js/auto'; // Required for chart.js

const StockTracker = () => {
  const [symbol, setSymbol] = useState('');
  const [priceData, setPriceData] = useState([]);
  const [optionData, setOptionData] = useState([]);
  const [toggleVolume, setToggleVolume] = useState(false); // Toggles between volume and $ amount

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchStockData(symbol);
  };

  const fetchStockData = async (symbol) => {
    try {
      const now = new Date();
      const tenMinutesAgo = new Date(now.getTime() - 10 * 60 * 1000);
      const fromDate = tenMinutesAgo.toISOString().split('T')[0];
      const toDate = now.toISOString().split('T')[0];

      // Fetch stock price data
      const priceResponse = await axios.get(
        `https://api.polygon.io/v2/aggs/ticker/${symbol}/range/1/minute/2024-09-20/${toDate}?adjusted=true&sort=asc&apiKey=_grbZQMP5kasPyiTvmUtApzQ5dapTGSu`
      );
      setPriceData(priceResponse.data.results || []);

      // Fetch option data for multiple strikes
      const optionPromises = [];
      const strikesRange = 10; // Fetch data for 10 strikes above and below
      for (let i = -strikesRange; i <= strikesRange; i++) {
        const strikePrice = (parseInt(symbol) + i * 10).toString(); // Adjust this based on your logic for strikes
        optionPromises.push(
          axios.get(
            `https://api.polygon.io/v2/aggs/ticker/O:${symbol}${strikePrice}/range/1/day/2024-09-10/${toDate}?adjusted=true&sort=asc&apiKey=_grbZQMP5kasPyiTvmUtApzQ5dapTGSu`
          )
        );
      }
      console.log(optionPromises);

      const optionResponses = await Promise.all(optionPromises);
      console.log(optionResponses);
      const optionsData = optionResponses.map((res) => res.data.results || []);

      setOptionData(optionsData.flat()); // Store option data

    } catch (error) {
      console.error('Error fetching stock or option data:', error);
    }
  };

  useEffect(() => {
    if (symbol) {
      const interval = setInterval(() => {
        fetchStockData(symbol);
      }, 1000); // Fetch every second
      return () => clearInterval(interval);
    }
  }, [symbol]);

  // Function to calculate Dollar Amount based on Volume and Contract Price
  const calculateDollarAmount = (volume, contractPrice) => volume * 100 * contractPrice;

  // Price chart configuration
  const priceChart = {
    labels: priceData.map((entry) => new Date(entry.t).toLocaleTimeString()),
    datasets: [
      {
        label: 'Stock Price',
        data: priceData.map((entry) => entry.c),
        fill: false,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  // Option Volume Chart Configuration
  const optionChart = {
    labels: optionData.map((entry) => new Date(entry.t).toLocaleTimeString()),
    datasets: [
      {
        label: 'Call Volume',
        data: optionData.map((entry) => toggleVolume ? calculateDollarAmount(entry.v, entry.c) : entry.v),
        backgroundColor: 'rgba(0, 255, 0, 0.5)', // Green for Calls
      },
      {
        label: 'Put Volume',
        data: optionData.map((entry) => toggleVolume ? calculateDollarAmount(entry.v, entry.p) : entry.v),
        backgroundColor: 'rgba(255, 0, 0, 0.5)', // Red for Puts
      },
    ],
  };

  // Function to toggle between showing volume or dollar amount in option chart
  const handleToggleVolume = () => {
    setToggleVolume((prevState) => !prevState);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter Stock Symbol"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
        />
        <button type="submit">Submit</button>
      </form>

      {/* Display stock price graph */}
      {priceData.length > 0 && (
        <>
          <h2>{symbol} Stock Price</h2>
          <Line data={priceChart} />
        </>
      )}

      {/* Toggle button for Volume to $ amount */}
      <button onClick={handleToggleVolume}>
        {toggleVolume ? 'Show Volume' : 'Show Dollar Amount'}
      </button>

      {/* Display options volume graph */}
      {optionData.length > 0 && (
        <>
          <h2>{symbol} Options Volume</h2>
          <Bar data={optionChart} />
        </>
      )}
    </div>
  );
};

export default StockTracker;
